# Function definitions 
## Set sampled parameter ranges to parameter boundaries
set_par_range <- function(par_tbl, range_tbl) {
  map2_df(par_tbl, range_tbl, ~ (.x * (.y[2] - .y[1]) + .y[1]))
}

